mapper.select:
SELECT EVENTTITLE, EVENTSUBTITLE, EVENTCONTENT, EVENTDATE
FROM TBL_EVENT_INFO
WHERE EVENTNUM = #{eventNum}

mapper.update:
UPDATE TBL_EVENT_INFO
SET EVENTTITLE = #{eventTitle}, EVENTSUBTITLE = #{eventSubtitle}, EVENTCONTENT = #{eventContent}, EVENTDATE = #{eventDate}
WHERE EVENTNUM = #{eventNum}
