mapper.update:
UPDATE TBL_INQUIRYREPLY
SET INQUIRYREPLY = #{inquiryReply}, INQUIRYREPLYUPDATEDATE = SYSDATE
WHERE INQUIRYRNO = #{inquiryRno}

mapper.select:

SELECT INQUIRYRNO, INQUIRYNUM, INQUIRYREPLY, INQUIRYREPLIER, INQUIRYREPLYDATE, INQUIRYREPLYUPDATEDATE
FROM
    (
    SELECT /*+ INDEX_DESC(TBL_INQUIRYREPLY PK_INQUIRYREPLY)*/
    ROWNUM R, INQUIRYRNO, INQUIRYNUM, INQUIRYREPLY, INQUIRYREPLIER, INQUIRYREPLYDATE, INQUIRYREPLYUPDATEDATE
    FROM TBL_INQUIRYREPLY
    WHERE INQUIRYNUM = #{inquiryNum}
    AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
    )
    WHERE R > (#{criteria.pageNum} -1) * #{criteria.amount}
