mapper.select:
SELECT ploResNum, memberNum, ploResLocation, ploggingNum, ploResDate,ploResTime,ploResStatus
FROM(

SELECT /** INDEX_DESC(TBL_PLO_RES RESERVATION_PK)*/
ROWNUM R, ploResNum, memberNum, ploResLocation, ploggingNum, ploResDate,ploResTime,ploResStatus
FROM TBL_PLO_RES WHERE memberNum = #{memberNum}
            ROWNUM <= #{pageNum} * #{amount}
        )
    WHERE R > (#{pageNum} - 1) * #{amount}

SELECT goodsLikeListNum, memberNum, goodsNum
FROM(

SELECT /** INDEX_DESC(TBL_GOODS_LIKE_LIST GOODS_LIKE_LIST_PK)*/
ROWNUM R,goodsLikeListNum, memberNum, goodsNum
FROM TBL_GOODS_LIKE_LIST
WHERE memberNum = #{memberNum}
                ROWNUM <= #{pageNum} * #{amount}
            )
        WHERE R > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE TBL_RESERVATION
SET reservationStatus = 1
WHERE ploResNum = #{ploResNum}

UPDATE TBL_MEMBER
SET memberPw = #{memberPw}, memberEmail = #{memberEmail}, memberZipcode = #{memberZipcode}, memberAddress = #{memberAddress}
WHERE memberNum = #{memberNum}

UPDATE TBL_MEMBER
SET status = 1
WHERE memberId = #{memberId} AND memberPw = #{memberPw}

mapper.select:

SELECT COMMUBNO, COMMUTITLE, COMMUCONTENT, COMMUWRITER, COMMUREGDATE, COMMUUPDATEDATE,COMMUVIEWCNT, COMMUBOARDSTATUS
FROM
    (
        SELECT /*+ INDEX_DESC(TBL_COMMUBOARD PK_COMMUBOARD)*/
        ROWNUM R, COMMUBNO, COMMUTITLE, COMMUCONTENT, COMMUWRITER, COMMUREGDATE, COMMUUPDATEDATE, COMMUVIEWCNT, COMMUBOARDSTATUS
        FROM TBL_COMMUBOARD
        WHERE COMMUBOARDSTATUS = 0 AND
        ROWNUM <= #{pageNum} * #{amount}
        AND COMMUWRITER = #{memberId}
    )
WHERE R > (#{pageNum} - 1) * #{amount}
