mapper.select:
SELECT orderNum,memberNum,goodsNum,goodsCnt,goodsColor,goodsSize,orderDate,
    recipient,recipientPhone,recipientZipCode,recipientAddress,recipientAddressEtc,
    parcelMessage,usePoint,orderStatus
    FROM (

SELECT /** INDEX_DESC(TBL_HISTORY_ORDER ORDERNUM_PK)*/
    ROWNUM R, orderNum,memberNum,goodsNum,goodsCnt,goodsColor,goodsSize,orderDate,
    recipient,recipientPhone,recipientZipCode,recipientAddress,recipientAddressEtc,
    parcelMessage,usePoint,orderStatus
FROM TBL_ORDER_HISTORY WHERE memberNum = #{memberNum}
                ROWNUM <= #{pageNum} * #{amount}
            )
        WHERE R > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE TBL_GOODS_ORDER
SET orderStatus = 1
WHERE orderNum = #{orderNum}
