mapper.select:
SELECT PLOGGINGTITLE, PLOGGINGCONTENT, PLOGGINGPPL, PLOGGINGPPLLIMIT
FROM TBL_PLOGGING_INFO
WHERE PLOGGINGNUM = #{ploggingNum}


SELECT PLOGGINGNUM, PLOGGINGTITLE, PLORESDATE
FROM
(
SELECT /*+ INDEX_DESC(TBL_EVENT_INFO EVENT_INFO_PK) */
ROWNUM R, PLOGGINGNUM, PLOGGINGTITLE, PLORESDATE
FROM TBL_PLOGGING_INFO
WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}
