SELECT pointNum, memberNum, history, point, pointDate, pointStatus
FROM
(
SELECT /*+ INDEX_DESC(TBL_POINT POINTNUM_PK)*/
ROWNUM R, pointNum, memberNum, history, point, pointDate, pointStatus
FROM TBL_POINT WHERE memberNum = #{memberNum} AND
ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
)
WHERE R > (#{criteria.pageNum} - 1) * #{criteria.amount}
