SELECT pointNum, memberId, history, point, pointDate, pointStatus
FROM
(
SELECT /*+ INDEX_DESC(TBL_POINT POINTNUM_PK)*/
ROWNUM R, pointNum, memberId, history, point, pointDate, pointStatus
FROM TBL_POINT WHERE memberId = #{memberId} AND
ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
)
WHERE R > (#{criteria.pageNum} - 1) * #{criteria.amount}
