mapper.select:

SELECT goodsNum, goodsName, goodsCategory, goodsPrice, goodsContent
FROM
(
    SELECT /*+ INDEX_DESC(TBL_GOODSLIST GoodsPK) */
    ROWNUM R, goodsNum, goodsName, goodsCategory, goodsPrice, goodsContent
    FROM TBL_GOODSLIST
    WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE TBL_GOODSLIST
SET goodsName= #{goodsName}, goodsCategory=#{goodsCategory}, goodsPrice=#{goodsPrice}, goodsContent=#{goodsContent}
WHERE goodsNum= #{goodsNum}
